#include "libft.h"

char	*ft_strncat(char *s1, const char *s2, size_t len)
{
	int i;
	int j;

	j = 0;
	i = 0;
	while (s1[j] != '\0')
		j++;
	while (len > 0 && s2[i] != '\0')
	{
		s1[j++] = s2[i++];
		len--;
	}
	s1[j] = '\0';
	return (s1);
}
